const snekfetch = require('snekfetch');

class RandomCat {
  /**
   * Gives you a cute cat!
   * @returns A promise with URL
   */
  cat() {
    return new Promise((resolve, reject) => {
      snekfetch.get('https://aws.random.cat/meow').then(data => resolve(data.body.file));
    });
  }
}

module.exports = new RandomCat();