const snekfetch = require('snekfetch');

class RandomDog {
  /**
   * Gives you a cute dog!
   * @returns A promise with URL
   */
  dog() {
    return new Promise((resolve, reject) => {
      snekfetch.get('https://random.dog/woof.json').then(data => resolve(data.body.url));
    });
  }
}

module.exports = new RandomDog();